	public static function getAll($Condition = array(), $currentPage = 1, $recordPerPage = 10, $order = 'id DESC') {
		$Items = Doctrine_Query::create ()->from ( __CLASS__ )->orderBy ( $order );
		if (count ( $Condition ))
			foreach ( $Condition as $key => $item ) {
				$Items->addWhere ( $key, $item );
			}
		$pager = new Doctrine_Pager ( $Items, $currentPage, $recordPerPage );
