		);
	}
	
	public static function getWithOutPage($Condition = array(), $limit = 0) {
		$Items = Doctrine_Query::create ()->from ( __CLASS__ )->orderBy ( 'orders ASC' )->limit ( $limit );
		foreach ( $Condition as $key => $item ) {
			$Items->addWhere ( $key, $item );
		}
